mapper.select:
SELECT r.require_id as require_id, r.require_title as require_title, r.content as content, r.priority as priority, r.urgent as urgent, r.status as status, r.create_user_id as create_user_id, r.create_time as create_time, r.modify_user_id as modify_user_id, r.modify_time as modify_time, r.parent_id as parent_id, r.is_del as is_del, tag.tag_id as tag_id, tag.tag_name as tag_name, tag.tag_desc as tag_desc FROM t_require r LEFT JOIN ( SELECT any_value(tag_id) tag_id, require_id FROM t_require_tag GROUP BY require_id ) a ON a.require_id = r.require_id AND r.is_del = 0 AND r.require_id NOT IN ( SELECT require_branch_id FROM t_require_merge ) LEFT JOIN t_tag tag ON a.tag_id = tag.tag_id AND tag.is_del =0 where 1 = 1
and r.require_title LIKE CONCAT('%',#{requireListCo.requireTitle},'%')
and r.status = #{requireListCo.status}
and tag.tag_id = #{requireListCo.tagId}
order by priority,urgent,status
